    public function getSourceContext()
    {
        return new Twig_Source("{% extends 'base.html.twig' %}

{% block title %}Participer à un trajet{% endblock %}
{% block stylesheets %}

{% endblock %}
{% block main %}
    <div class=\"panel panel-info\">
        <h1>Les trajets disponible</h1>
    </div>
    {% if travels %}
        {% for travel in travels %}
            <div class=\"btn\"> Titre de l'annonce</div>:
            <div class=\"btn btn-1\"><h2>{{ travel.title }}</h2></div><br>
            <div class=\"active\">Date de création de l'annonce: {{ travel.datecreated|date }}</div>

            <div class=\"btn\"> Auteur</div>:
            <div class=\"btn btn-1\">{{ travel.user.firstname }} {{ travel.user.name|upper }}</div><br>
            <div class=\"btn\"> Ville de départ</div>:
            <div class=\"btn btn-1\">{{ travel.startcity }} {{ travel.endzipcode }}</div><br>
            <div class=\"btn\"> Heure de départ</div>:
            <div class=\"btn btn-1\">{{ travel.starthour|date }}</div><br>
            -------<br>
            <div class=\"btn\"> Ville d'arrivée</div>:
            <div class=\"btn btn-1\">{{ travel.endaddress }} {{ travel.endcity }}</div><br>
            <div class=\"btn\"> Prix du trajet</div>:
            <div class=\"btn btn-1\">{{ travel.price }} Euros</div><br>
            {% if travel.passengers.contains(user) %}
                <a class=\"btn btn-danger\" href=\"{{ path('travel_remove', {\"id\": travel.id}) }}\">Ne plus faire parti du
                    trajet</a>
            {% else %}
                <form target=\"paypal\" action=\"https://www.paypal.com/cgi-bin/webscr\" method=\"post\">

                    <!-- Identify your business so that you can collect the payments. -->
                    <input type=\"hidden\" name=\"business\" value=\"kin@kinskards.com\">

                    <!-- Specify a PayPal Shopping Cart Add to Cart button. -->
                    <input type=\"hidden\" name=\"cmd\" value=\"_cart\">
                    <input type=\"hidden\" name=\"add\" value=\"1\">

                    <!-- Specify details about the item that buyers will purchase. -->
                    <input type=\"hidden\" name=\"item_name\" value=\"{{ travel.title }}\">
                    <input type=\"hidden\" name=\"amount\" value=\"{{ travel.price }}\">
                    <input type=\"hidden\" name=\"currency_code\" value=\"USD\">

                    <!-- Display the payment button. -->
                    <input type=\"image\" name=\"submit\"
                           src=\"https://www.paypalobjects.com/en_US/i/btn/btn_cart_LG.gif\"
                           alt=\"Valider le trajet\">
                    <img alt=\"\" id=\"imgPaypal\"
                         src=\"https://www.paypalobjects.com/en_US/i/scr/pixel.gif\">
                </form>

                <a class=\"btn btn-success\" href=\"{{ path('travel_add', {\"id\": travel.id }) }}\">Participer au trajet</a>
                <form action=\"question/{{ travel.id }}\">
                    <button class=\"btn btn-secondary\">Posez une question</button>
                </form>
                <div id=\"questionsTravel\">
<h4>Questions</h4>
                    {% for question in travel.questions %}
                        <h5>Par {{ question.user.username }}</h5>
                        <p> {{ question.content }} </p>
                    {% endfor %}
                </div>

            {% endif %}
            <hr>
        {% endfor %}
    {% else %}
        <div class=\"btn btn-error\">There are actually no travel</div>
    {% endif %}
{% endblock %}
", "travel/travel_participate.html.twig", "/var/www/html/papoteCar/templates/travel/travel_participate.html.twig");
    }
